    //批量制作样本
    public function buildSample()
    {

        $Sample=new SampleModel();

        $sCount=$Sample->count();

        if($sCount==0)
        {

        }
        else
        {
            $whereS=array();

            $whereS["id"]=array(array('egt',0),array('lt',$sCount-1));

            $Sample->where($whereS)->delete();
        }

        $UserFDB=new UserFDBModel();

        $allCount=$UserFDB->group('name')->field("name")->select();



        $sampleNumber=intval(count($allCount)/2);


        $sampleVersion=time();

        for($i=0;$i<$sampleNumber;$i++)
        {
            $data["name"]="样本用户".$i;
            $data["sex"]=$this->randomSex();
            $data["marry"]=$this->randomMarrage();
            $data["age"]=$this->randomAge();
            $data["child"]=$this->randomFamily($data["marry"]);

            $where=array();

            $where["name"]=$allCount[$i]["name"];

            $rst=$UserFDB->where($where)->select();

            $str="";

            for($j=0;$j<count($rst);$j++)
            {
                //price":"100","timestamp":"1508342400"

                if($j==0)
                {
                    $str=$rst[$j]["price"].":".$rst[$j]["timestamp"];
                }
                else
                {
                    $str=$str.";".$rst[$j]["price"].":".$rst[$j]["timestamp"];
                }

            }

            $data["investhistory"]=$str;

            $data["income"]=$this->randomInCome($rst);

            $data["sampleversion"]=$sampleVersion;

            $Sample->add($data);

        }

    }

    //预测年收入
    private function randomInCome($rst)
    {
        $count=count($rst);

        $income=0;

        if($count==1)
        {
            $income=$rst[0]["price"]*rand(1,2);

            return $income;
        }
        else
        {


            for($i=0;$i<$count;$i++)
            {
                $income=$income+$rst[$i]["price"]*rand(1,2);
            }

            return intval($income/$count);
        }
    }

