    //https://www.dservie.cn/myWeb/index.php/BaoXian/BaoXianSB/currentSample
    public function currentSample($expertid = 1)
    {
        $this->ChildAgeRatio = 0.5;
        $this->MateRatio = 1;

        $Sample=new SampleModel();

        $sAllCount=$Sample->count();

        $ESample=new ESampleModel();

        $where=array();

        $where["expertid"]=$expertid;

        $readCount=$ESample->where($where)->count();

        if($readCount==0)
        {
            $idArray=$Sample->field("id")->select();

            $count=count($idArray);

            for($i=0;$i<$count;$i++)
            {
                $data[$i]["sampleid"]=$idArray[$i]["id"];
                $data[$i]["expertid"]=$expertid;
            }

            $ESample->addAll($data);

            $where["result"]=['exp','is null'];

            $sampleNow=$Sample->where($where)->order('id asc')->find();

        }
        else
        {
            $where["result"]=['exp','is null'];

            $sampleNow=$Sample->where($where)->order('id asc')->find();
        }

        // 计算配偶和小孩年龄，性别
        $sampleNow['insuredPeople']['holder']['age'] = $sampleNow['age'];
        $sampleNow['insuredPeople']['holder']['label'] = $sampleNow['name'];
        $sampleNow['insuredPeople']['holder']['sex'] = $sampleNow['sex'] === '0' ? 'male' : 'female';

        if ((int)$sampleNow['marry'] > 0) {
            $sampleNow['insuredPeople']['mate']['age'] = $this->MateRatio * $sampleNow['age'];
            $sampleNow['insuredPeople']['mate']['label'] = '配偶';
            $sampleNow['insuredPeople']['mate']['sex'] = $sampleNow['sex'] === '0' ? 'female' : 'male';
        }

        $sampleNow['insuredPeople']['children'] = [];

        if ((int)$sampleNow['child'] > 0) {
            for ($count = 0; $count < (int)$sampleNow['child']; $count ++) {
                $sampleNow['insuredPeople']['children'][$count]['age'] = $this->ChildAgeRatio * $sampleNow['age'];
                $sampleNow['insuredPeople']['children'][$count]['sex'] = 'boy';
                $sampleNow['insuredPeople']['children'][$count]['label'] = '孩子' . ($count + 1) ;
            }
        }

        $feedBack["allSampleCount"]=$sAllCount;
        $feedBack["readySample"]=$readCount;
        $feedBack["nowSample"]=$sampleNow;

        return $feedBack;
//        print '<pre>';
//        var_dump($feedBack['nowSample']);exit;
//
//        response("Success",1,$feedBack);

    }

